import React, { useEffect } from 'react';
import sliderBackground from '../assets/images/slider-bg1.jpg'; // Import the image
import WOW from 'wow.js';

const Slider = () => {
    useEffect(() => {
        new WOW().init();
        initHeadline();
      }, []);

      const initHeadline = () => {
        const words = document.querySelectorAll('.cd-headline.letters b');
        singleLetters(words);
        animateHeadline(words);
      };

      const singleLetters = (words) => {
        words.forEach((word) => {
          let letters = word.innerText.split('');
          const selected = word.classList.contains('is-visible');
          letters = letters.map((letter, index) => {
            return selected
              ? `<i class="in">${letter}</i>`
              : `<i>${letter}</i>`;
          });

          word.innerHTML = letters.join('');
          word.style.opacity = 1;
        });
      };

      const animateHeadline = (headlines) => {
        headlines.forEach((headline) => {
          const duration = headline.classList.contains('loading-bar')
            ? 3800
            : 2500;

          if (headline.classList.contains('loading-bar')) {
            setTimeout(() => {
              headline.querySelector('.cd-words-wrapper').classList.add('is-loading');
            }, 3800 - 3000);
          } else if (headline.classList.contains('clip')) {
            const spanWrapper = headline.querySelector('.cd-words-wrapper');
            const newWidth = spanWrapper.offsetWidth + 10;
            spanWrapper.style.width = `${newWidth}px`;
          }

          setTimeout(() => {
            hideWord(headline.querySelector('.is-visible'));
          }, duration);
        });
      };

      const hideWord = (word) => {
        const nextWord = takeNext(word);
        if (word.closest('.cd-headline').classList.contains('type')) {
          const parentSpan = word.closest('.cd-words-wrapper');
          parentSpan.classList.add('selected');
          parentSpan.classList.remove('waiting');
          setTimeout(() => {
            parentSpan.classList.remove('selected');
            word.classList.remove('is-visible');
            word.classList.add('is-hidden');
            word.querySelector('i').classList.remove('in');
            word.querySelector('i').classList.add('out');
          }, 500);

          setTimeout(() => {
            showWord(nextWord, 150);
          }, 800);
        }
        // Implement other animation types like 'letters', 'clip', 'loading-bar'
      };

      const showWord = (word, duration) => {
        if (word.closest('.cd-headline').classList.contains('type')) {
          showLetter(word.querySelector('i'), word, false, duration);
          word.classList.add('is-visible');
          word.classList.remove('is-hidden');
        }
        // Implement other animation types
      };

      const showLetter = (letter, word, bool, duration) => {
        letter.classList.add('in');
        letter.classList.remove('out');
        if (letter !== word.querySelector('i:last-child')) {
          setTimeout(() => {
            showLetter(letter.nextElementSibling, word, bool, duration);
          }, duration);
        }
      };

      const takeNext = (word) => {
        return !word.nextElementSibling
          ? word.parentElement.children[0]
          : word.nextElementSibling;
      };

  return (
    <div
      className="slider_area creative minimal-2"
      id="home"
      style={{
        backgroundImage: `url(${sliderBackground})`, // Set background dynamically
        backgroundSize: 'cover',
        backgroundPosition: 'center center',
        position: 'relative',
        zIndex: 1,
      }}
    >      <div className="container">
        <div className="row">
          <div className="col-md-12">
            {/* Single Slide */}
            <div className="single_slide">
              <div className="slider_content">
                <div className="slider_text">
                  <div className="slider_text_inner">
                    <h1 className="cd-headline clip is-full-width">
                      <span className="cd-words-wrapper">
                        <b className="is-visible">EMILIO GAMBONE</b>
                        <b>IT ENGINEER</b>
                        {/* <b>Web DEVELOPER</b> */}
                      </span>
                    </h1>
                    <ul>
                      <li>Solutions Architect</li>
                      <li>IT Consultant</li>
                      {/* <li>Freelancer</li> */}
                    </ul>
                  </div>
                </div>

                <div
                  className="scroll-next text-center wow infinite fadeInDown"
                  data-wow-duration="2s"
                >
                  <a href="#about">
                    <i className="fa fa-angle-double-down"></i>
                  </a>
                </div>
              </div>
            </div>
          </div>
          <div className="single_slider_icon">
            <div className="slider_icon_inner">
              <a href="https://github.com/emiliogambone">
                <i className="fa-brands fa-github"></i>
              </a>
              <a href="https://www.linkedin.com/in/emilio-gambone-41624458/">
                <i className="fa-brands fa-linkedin-in"></i>
              </a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Slider;
