import React, { useLayoutEffect, useRef } from 'react';
import sliderBackground from '../assets/images/slider-bg1.jpg'; // Import the image

const Slider = () => {
    const headlineRef = useRef(null); // Ref for the headline

    const animationDelay = 2500;
    const barAnimationDelay = 3800;
    const barWaiting = barAnimationDelay - 3000;
    const lettersDelay = 50;
    const typeLettersDelay = 150;
    const selectionDuration = 500;
    const typeAnimationDelay = selectionDuration + 800;
    const revealDuration = 600;
    const revealAnimationDelay = 1500;

    useLayoutEffect(() => {
        console.log("Component Mounted");

        if (headlineRef.current) {
          console.log("Headline ref is available:", headlineRef.current);
          initHeadline();
        } else {
          console.log("Headline ref is NOT available yet");
        }

        return () => {
          console.log("Component Unmounted");
        };
      }, []); // Empty dependency array means this effect runs once on mount


  // Initialize the headline text animation
  const initHeadline = () => {
    const words = headlineRef.current.querySelectorAll('.cd-headline.letters b');
    singleLetters(words);
    animateHeadline(words);
  };

  const singleLetters = (words) => {
    words.forEach((word) => {
      let letters = word.innerText.split('');
      const selected = word.classList.contains('is-visible');
      letters = letters.map((letter) => {
        return selected ? `<i class="in">${letter}</i>` : `<i>${letter}</i>`;
      });

      word.innerHTML = letters.join('');
      word.style.opacity = 1;
    });
  };

  const animateHeadline = (headlines) => {
    headlines.forEach((headline) => {
      let duration = animationDelay;
      if (headline.classList.contains('loading-bar')) {
        duration = barAnimationDelay;
        setTimeout(() => {
          headline.querySelector('.cd-words-wrapper').classList.add('is-loading');
        }, barWaiting);
      } else if (headline.classList.contains('clip')) {
        const spanWrapper = headline.querySelector('.cd-words-wrapper');
        const newWidth = spanWrapper.offsetWidth + 10;
        spanWrapper.style.width = `${newWidth}px`;
      }

      setTimeout(() => {
        hideWord(headline.querySelector('.is-visible'));
      }, duration);
    });
  };

  const hideWord = (word) => {
    const nextWord = takeNext(word);
    if (word.closest('.cd-headline').classList.contains('type')) {
      const parentSpan = word.closest('.cd-words-wrapper');
      parentSpan.classList.add('selected');
      parentSpan.classList.remove('waiting');
      setTimeout(() => {
        parentSpan.classList.remove('selected');
        word.classList.remove('is-visible');
        word.classList.add('is-hidden');
        word.querySelector('i').classList.remove('in');
        word.querySelector('i').classList.add('out');
      }, selectionDuration);

      setTimeout(() => {
        showWord(nextWord, typeLettersDelay);
      }, typeAnimationDelay);
    } else if (word.closest('.cd-headline').classList.contains('letters')) {
      const bool = word.querySelector('i').length >= nextWord.querySelector('i').length;
      hideLetter(word.querySelector('i'), word, bool, lettersDelay);
      showLetter(nextWord.querySelector('i'), nextWord, bool, lettersDelay);
    } else if (word.closest('.cd-headline').classList.contains('clip')) {
      word.closest('.cd-words-wrapper').animate({ width: '2px' }, revealDuration, () => {
        switchWord(word, nextWord);
        showWord(nextWord);
      });
    } else if (word.closest('.cd-headline').classList.contains('loading-bar')) {
      word.closest('.cd-words-wrapper').classList.remove('is-loading');
      switchWord(word, nextWord);
      setTimeout(() => {
        hideWord(nextWord);
      }, barAnimationDelay);

      setTimeout(() => {
        word.closest('.cd-words-wrapper').classList.add('is-loading');
      }, barWaiting);
    } else {
      switchWord(word, nextWord);
      setTimeout(() => {
        hideWord(nextWord);
      }, animationDelay);
    }
  };

  const showWord = (word, duration) => {
    if (word.closest('.cd-headline').classList.contains('type')) {
      showLetter(word.querySelector('i'), word, false, duration);
      word.classList.add('is-visible');
      word.classList.remove('is-hidden');
    } else if (word.closest('.cd-headline').classList.contains('clip')) {
      word.closest('.cd-words-wrapper').animate({ width: word.offsetWidth + 10 }, revealDuration, () => {
        setTimeout(() => {
          hideWord(word);
        }, revealAnimationDelay);
      });
    }
  };

  const hideLetter = (letter, word, bool, duration) => {
    letter.classList.remove('in');
    letter.classList.add('out');
    if (letter !== word.querySelector('i:last-child')) {
      setTimeout(() => {
        hideLetter(letter.nextElementSibling, word, bool, duration);
      }, duration);
    } else if (bool) {
      setTimeout(() => {
        hideWord(takeNext(word));
      }, animationDelay);
    }
    if (letter === word.querySelector('i:last-child') && document.documentElement.classList.contains('no-csstransitions')) {
      const nextWord = takeNext(word);
      switchWord(word, nextWord);
    }
  };

  const showLetter = (letter, word, bool, duration) => {
    letter.classList.add('in');
    letter.classList.remove('out');
    if (letter !== word.querySelector('i:last-child')) {
      setTimeout(() => {
        showLetter(letter.nextElementSibling, word, bool, duration);
      }, duration);
    } else {
      if (word.closest('.cd-headline').classList.contains('type')) {
        setTimeout(() => {
          word.closest('.cd-words-wrapper').classList.add('waiting');
        }, 200);
      }
      if (!bool) {
        setTimeout(() => {
          hideWord(word);
        }, animationDelay);
      }
    }
  };

  const takeNext = (word) => {
    return !word.nextElementSibling ? word.parentElement.children[0] : word.nextElementSibling;
  };

  const switchWord = (oldWord, newWord) => {
    oldWord.classList.remove('is-visible');
    oldWord.classList.add('is-hidden');
    newWord.classList.remove('is-hidden');
    newWord.classList.add('is-visible');
  };

    return (
    <div
      className="slider_area creative minimal-2"
      id="home"
      style={{
        backgroundImage: `url(${sliderBackground})`, // Set background dynamically
        backgroundSize: 'cover',
        backgroundPosition: 'center center',
        position: 'relative',
        zIndex: 1,
      }}
    >      <div className="container">
        <div className="row">
          <div className="col-md-12">
            {/* Single Slide */}
            <div className="single_slide">
              <div className="slider_content">
                <div className="slider_text">
                  <div className="slider_text_inner">
                    <h1 className="cd-headline clip is-full-width" ref={headlineRef}>
                      <span className="cd-words-wrapper">
                        <b className="is-visible">EMILIO GAMBONE</b>
                        <b>IT ENGINEER</b>
                        {/* <b>Web DEVELOPER</b> */}
                      </span>
                    </h1>
                    <ul>
                      <li>Solutions Architect</li>
                      <li>IT Consultant</li>
                      {/* <li>Freelancer</li> */}
                    </ul>
                  </div>
                </div>

                <div
                  className="scroll-next text-center wow infinite fadeInDown"
                  data-wow-duration="2s"
                >
                  <a href="#about">
                    <i className="fa fa-angle-double-down"></i>
                  </a>
                </div>
              </div>
            </div>
          </div>
          <div className="single_slider_icon">
            <div className="slider_icon_inner">
              <a href="https://github.com/emiliogambone">
                <i className="fa-brands fa-github"></i>
              </a>
              <a href="https://www.linkedin.com/in/emilio-gambone-41624458/">
                <i className="fa-brands fa-linkedin-in"></i>
              </a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Slider;
